package uz.coder.uzmovie.adaptersimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport androidx.viewpager2.widget.ViewPager2import com.squareup.picasso.Callbackimport com.squareup.picasso.Picassoimport uz.coder.uzmovie.databinding.ItemBindingimport uz.coder.uzmovie.databinding.ViewPagerItemBindingimport uz.coder.uzmovie.models.MovieClassimport java.lang.Exceptionclass MyAdapter(private val arraylist: ArrayList<MovieClass>, val onClick: OnClick) :    RecyclerView.Adapter<MyAdapter.ViewH>() {    inner class ViewH(var binding: ItemBinding) : RecyclerView.ViewHolder(binding.root) {        fun onBind(myClass: MovieClass) {            Picasso.get().load("https://image.tmdb.org/t/p/w500/" + myClass.poster_path).into(binding.image, object :Callback{                override fun onSuccess() {                    binding.shimmerView.visibility = View.GONE                }                override fun onError(e: Exception?) {                }            })            binding.title.text = myClass.title            binding.info.text = myClass.overview            itemView.setOnClickListener { onClick.click(myClass) }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewH {        return ViewH(            ItemBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun getItemCount(): Int {        return arraylist.size    }    override fun onBindViewHolder(holder: ViewH, position: Int) {        holder.onBind(arraylist[position])    }    interface OnClick{        fun click(myClass: MovieClass)    }}