package uz.coder.uzmovie.adaptersimport androidx.fragment.app.Fragmentimport androidx.viewpager2.adapter.FragmentStateAdapterimport uz.coder.uzmovie.ui.fragments.HomeFragmentimport uz.coder.uzmovie.ui.fragments.ProfileFragmentimport uz.coder.uzmovie.ui.fragments.SavedFragmentclass ViewPagerAdapter(    fragment: Fragment) : FragmentStateAdapter(fragment) {    override fun getItemCount(): Int {        return 3    }    override fun createFragment(position: Int): Fragment {        when (position) {            0 -> return HomeFragment()            1 -> return SavedFragment()        }        return ProfileFragment()    }}