package uz.coder.uzmovie.databaseimport androidx.room.TypeConverterimport com.google.gson.Gsonimport com.google.gson.reflect.TypeTokenclass DataConvertor {    @TypeConverter    fun fromString(str: String): List<Int> {        val list = object : TypeToken<List<Int>>() {}.type        return Gson().fromJson(str, list)    }    @TypeConverter    fun fromList(list: List<Int>): String {        val gson = Gson()        return gson.toJson(list)    }}