package uz.coder.uzmovie.repositoryimport uz.coder.uzmovie.networks.ApiServiceclass MovieRepository(private val apiService: ApiService) {    suspend fun getPopularVideos(        page: Int    ) = apiService.getPopular(page = page)    suspend fun getSearchMovies(        query: String,    ) = apiService.getSearchMovies(query = query)    suspend fun getVideos(        movie_id: Int    ) = apiService.getVideos(movie_id = movie_id)}